import argparseimport ec2if __name__ == "__main__":    argparser = argparse.ArgumentParser(description="")    subparsers = argparser.add_subparsers()    parser_init = subparsers.add_parser("init", help="")    parser_init.add_argument("project", help="")    parser_init.add_argument("stage", help="")    parser_init.set_defaults(func=ec2.init_environment)    parser_destroy = subparsers.add_parser("destroy", help="")    parser_destroy.add_argument("project", help="")    parser_destroy.add_argument("stage", help="")    parser_destroy.set_defaults(func=ec2.destroy_environment)    args = argparser.parse_args()    args.func(args)