import zebr0import ec2import route53def init_environment(zebr0_service):    ec2_service = ec2.Service(zebr0_service)    vpc_id = ec2_service.create_vpc_if_needed()    subnet_id = ec2_service.create_subnet_if_needed(vpc_id)    ec2_service.create_internet_gateway_if_needed(vpc_id)    instance_id = ec2_service.create_instance_if_needed(subnet_id)    address = ec2_service.create_address_if_needed(instance_id)    route53_service = route53.Service(zebr0_service)    route53_service.create_dns_entry_if_needed(address)def destroy_environment(zebr0_service):    route53_service = route53.Service(zebr0_service)    route53_service.destroy_dns_entry_if_needed()    ec2_service = ec2.Service(zebr0_service)    ec2_service.destroy_address_if_needed()    ec2_service.destroy_instance_if_needed()    ec2_service.destroy_internet_gateway_if_needed()    ec2_service.destroy_subnet_if_needed()    ec2_service.destroy_vpc_if_needed()if __name__ == "__main__":    argparser = zebr0.ArgumentParser()    subparsers = argparser.add_subparsers()    subparsers.add_parser("init").set_defaults(func=init_environment)    subparsers.add_parser("destroy").set_defaults(func=destroy_environment)    args = argparser.parse_args()    args.func(zebr0.Service(args))