import argparseimport zebr0libimport ec2import route53def init_environment(config):    ec2_service = ec2.Service(config)    vpc_id = ec2_service.create_vpc_if_needed()    subnet_id = ec2_service.create_subnet_if_needed(vpc_id)    ec2_service.create_internet_gateway_if_needed(vpc_id)    instance_id = ec2_service.create_instance_if_needed(subnet_id)    address = ec2_service.create_address_if_needed(instance_id)    route53_service = route53.Service(config)    route53_service.create_dns_entry_if_needed(address)def destroy_environment(config):    route53_service = route53.Service(config)    route53_service.destroy_dns_entry_if_needed()    ec2_service = ec2.Service(config)    ec2_service.destroy_address_if_needed()    ec2_service.destroy_instance_if_needed()    ec2_service.destroy_internet_gateway_if_needed()    ec2_service.destroy_subnet_if_needed()    ec2_service.destroy_vpc_if_needed()if __name__ == "__main__":    argparser = argparse.ArgumentParser(description="")    subparsers = argparser.add_subparsers()    subparsers.add_parser("init", help="").set_defaults(func=init_environment)    subparsers.add_parser("destroy", help="").set_defaults(func=destroy_environment)    argparser.add_argument("project", help="")    argparser.add_argument("stage", help="")    argparser.add_argument("--debug", action="store_true", help="")    args = argparser.parse_args()    zebr0lib.setup_logs(args.debug)    config = zebr0lib.Config(args.project, args.stage)    args.func(config)