import zebr0import ec2import route53def create():    ec2_service = ec2.Service(zebr0_service)    vpc_id = ec2_service.create_vpc_if_needed()    subnet_id = ec2_service.create_subnet_if_needed(vpc_id)    ec2_service.create_internet_gateway_if_needed(vpc_id)    instance_id = ec2_service.create_instance_if_needed(subnet_id)    address = ec2_service.create_address_if_needed(instance_id)    route53_service = route53.Service(zebr0_service)    route53_service.create_dns_entry_if_needed(address)def delete():    route53_service = route53.Service(zebr0_service)    route53_service.destroy_dns_entry_if_needed()    ec2_service = ec2.Service(zebr0_service)    ec2_service.destroy_address_if_needed()    ec2_service.destroy_instance_if_needed()    ec2_service.destroy_internet_gateway_if_needed()    ec2_service.destroy_subnet_if_needed()    ec2_service.destroy_vpc_if_needed()argparser = zebr0.ArgumentParser()argparser.add_argument("command", choices=["create", "delete"])args = argparser.parse_args()zebr0_service = zebr0.Service(args)# calls the method given as parameterglobals()[args.command]()